part of 'timer_bloc.dart';abstract class TimerState extends Equatable {  final Duration duration;  const TimerState(this.duration);  @override  List<Object> get props => [duration];}class TimerInitial extends TimerState {  const TimerInitial(Duration duration) : super(duration);  @override  String toString() => 'TimerInitial { duration: $duration }';}class TimerRunPause extends TimerState {  const TimerRunPause(Duration duration) : super(duration);  @override  String toString() => 'TimerRunPause { duration: $duration }';}class TimerRunInProgress extends TimerState {  const TimerRunInProgress(Duration duration) : super(duration);  @override  String toString() => 'TimerRunInProgress { duration: $duration }';}class TimerRunComplete extends TimerState {  const TimerRunComplete() : super(const Duration(seconds: 0, minutes: 1));}